import React from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import axios from 'axios'; // <-- 2. IMPORTAR AXIOS Y useNavigate
import '../../styles/components/layout/Nav.css';

// --- 1. RECIBIR 'user' Y 'onLogout' ---
const Nav = ({ user, onLogout }) => {
  
  const navigate = useNavigate();

  // --- 3. CREAR FUNCIÓN DE LOGOUT ---
  const handleLogout = async () => {
    try {
      // Llama a la API del backend para destruir la sesión
      await axios.get(`${process.env.REACT_APP_API_URL}/api/logout`, {
        withCredentials: true
      });
      // Llama a onLogout(null) (que es setUser(null)) para limpiar el estado global
      onLogout(null); 
      // Redirige al Home
      navigate('/'); 
    } catch (error) {
      console.log("Error al cerrar sesión", error);
    }
  };

  return (
    <nav>
      <div className="holder">
        <ul>
          <li><NavLink to="/" className={({ isActive }) => isActive ? 'activo' : ''}>Home</NavLink></li>
          <li><NavLink to="/nosotros" className={({ isActive }) => isActive ? 'activo' : ''}>Nosotros</NavLink></li>
          <li><NavLink to="/novedades" className={({ isActive }) => isActive ? 'activo' : ''}>Novedades</NavLink></li>
          <li><NavLink to="/contacto" className={({ isActive }) => isActive ? 'activo' : ''}>Contacto</NavLink></li>
          
          {/* --- 4. MOSTRAR LOGIN O LOGOUT --- */}
          {user ? (
            // Si hay un usuario, muestra el botón de Logout
            <li>
              <button 
                onClick={handleLogout} 
                style={{ background: 'none', border: 'none', color: 'white', cursor: 'pointer', padding: '0', fontFamily: 'inherit', fontSize: 'inherit' }}
              >
                Logout
              </button>
            </li>
          ) : (
            // Si no hay usuario, muestra el link de Login
            <li><NavLink to="/login" className={({ isActive }) => isActive ? 'activo' : ''}>Login</NavLink></li>
          )}
          
        </ul>
      </div>
    </nav>
  );
}

export default Nav;
