import React from 'react';
import '../../styles/components/layout/Header.css';

const Header = ({ user }) => { 
  return (
    <header>
      <div className="holder">
        
        {/* --- CORRECCIÓN AQUÍ --- */}
        {/* La ruta debe ser /images/logo.png (desde la carpeta 'public') */}
        <img src="/images/logo.png" width="200" alt="Gaming Store" />

        <h1>Gaming Store</h1>
        
        {user && (
          <div className="usuario-info" style={{ 
            position: 'absolute', 
            top: '20px', 
            right: '20px', 
            color: 'white',
            backgroundColor: 'rgba(0,0,0,0.3)',
            padding: '10px',
            borderRadius: '5px'
          }}>
            Hola, <strong>{user.nombre}</strong>
            {user.rol === 'admin' ? ' (Admin)' : ''}
          </div>
        )}
      </div>
    </header>
  );
}

export default Header;
