import React from 'react';
import '../../styles/components/layout/Header.css';

// --- 1. RECIBIR 'user' DE LAS PROPS ---
const Header = ({ user }) => { 
  return (
    <header>
      <div className="holder">
        <img src="/images/logo.png" width="100" alt="Transportes X" />
        <h1>Gaming Store</h1>
        
        {/* --- 2. MOSTRAR INFO DEL USUARIO (SI EXISTE) --- */}
        {user && (
          <div className="usuario-info" style={{ 
            position: 'absolute', 
            top: '20px', 
            right: '20px', 
            color: 'white',
            backgroundColor: 'rgba(0,0,0,0.3)',
            padding: '10px',
            borderRadius: '5px'
          }}>
            Hola, <strong>{user.nombre}</strong>
            
            {/* 3. MOSTRAR ROL DE ADMIN */}
            {user.rol === 'admin' ? ' (Admin)' : ''}
          </div>
        )}
      </div>
    </header>
  );
}

export default Header;
